from shiny import App, render, ui


app_ui = ui.page_navbar(
    ui.nav(
        "Search",
        ui.row(
            ui.column(
                12,
                ui.input_selectize(
                    id="search_selectize",
                    label="Visuals",
                    width="100%",
                    choices={
                        "AAPL": "Apple Inc.",
                        "NFLX": "Netflix Inc.",
                        "GOOG": "Google Inc.",
                    },
                    multiple=False,
                ),
                ui.output_text("search_value"),
            )
        ),
    ),
    ui.nav(
        "Build",
        ui.row(
            ui.column(
                12,
            )
        ),
    ),
    title="SCRAPEABLE MVP",
    bg="#5096cf",
    inverse=True,
    position="static-top",
    collapsible=True,
)


def server(input, output, session):
    @output
    @render.text
    def search_value():
        return "You choose: " + str(input.search_selectize())


app = App(app_ui, server)
