from shiny import App, render, ui

app_ui = ui.page_navbar(
    ui.nav(
        "Search",
        ui.row(
            ui.input_selectize(
                id="search_selectize",
                label="Visuals",
                width="100%",
                choices={
                    "AAPL": "Apple Inc.",
                    "NFLX": "Netflix Inc.",
                    "GOOG": "Google Inc.",
                },
                multiple=False,
            ),
            ui.output_text("search_value"),
        )
    ),
    ui.nav(
        "Build",
        ui.tags.p(
            "Use Posit's shinylive framework to build web apps using Scrapeable data"
        ),
        ui.tags.iframe(
            src="https://shinylive.io/py/editor/#",
            height="600px",
            width="100%",
        )
    ),
    title="SCRAPEABLE MVP",
    bg="#5096cf",
    inverse=True,
    position="static-top",
    collapsible=True,
)


def server(input, output, session):
    @output
    @render.text
    def search_value():
        return "You choose: " + str(input.search_selectize())


app = App(app_ui, server)
